# Gets time stamps for all computers in the domain that have NOT logged in since after specified date
# Mod by Satish V. on 2017-09-22
import-module activedirectory 
$domain = "vel.co.in"
$DaysInactive = 30
$time = (Get-Date).Adddays(-($DaysInactive))
$date = Get-Date;

# Get Computers
Get-ADComputer -filter * -Properties LastLogonDate,Name,OperatingSystem,Created|

# Filters such as last login
Where-Object {$_.LastLogonDate -lt $date.AddDays(-30)}|
Where-Object {$_.Created -lt $date.AddDays(-30)}| 

# Write results
Select Name,OperatingSystem,DistinguishedName,Created,LastLogonDate,DNSHostName,Enabled|
export-csv -Path “E:\SCRIPT_OUTPUT\DISABLED COMPUTERS\Computers_$((Get-Date).ToString('MM-dd-yyyy_hh-mm-ss')).csv”

# Get all AD computers with lastLogonTimestamp less than our time
Get-ADComputer -SearchBase “OU=CROMA-SM-COMPUTERS,OU=DESKTOPS,DC=vel,DC=co,DC=in” -Filter {LastLogonTimeStamp -lt $time} -Properties LastLogonTimeStamp | Move-ADObject -TargetPath "OU=DISABLED COMPUTERS,DC=vel,DC=co,DC=in"
Get-ADComputer -SearchBase “OU=IR-SO-LAPTOPS,OU=DESKTOPS,DC=vel,DC=co,DC=in” -Filter {LastLogonTimeStamp -lt $time} -Properties LastLogonTimeStamp | Move-ADObject -TargetPath "OU=DISABLED COMPUTERS,DC=vel,DC=co,DC=in"
